mapper.select:
SELECT G.PRGRM_CD, G.GC_OG_NM, G.USER_NO, G.NOPE, DATE_FORMAT(G.SCHDL_BGNG_YMD, '%Y-%m-%d %H시') AS SCHDL_BGNG_YMD, DATE_FORMAT(G.SCHDL_END_YMD, '%Y-%m-%d %H시') AS SCHDL_END_YMD, DATE_FORMAT(G.GC_DT, '%Y-%m-%d %H시') AS GC_DT, G.PRGRM_NM, G.PRGRM_TYPE, G.GC_CONTS_CN, G.GC_DTL_CN, COALESCE(APPLY_CNT.COUNT, 0) AS APPLY_CNT FROM GC_PROGRAM G LEFT JOIN ( SELECT PRGRM_CD, COUNT(*) AS COUNT FROM PRGRM_APP GROUP BY PRGRM_CD ) APPLY_CNT ON G.PRGRM_CD = APPLY_CNT.PRGRM_CD WHERE G.PRGRM_NM LIKE CONCAT('%', #{searchWord}, '%')
AND #{nowDate} BETWEEN G.SCHDL_BGNG_YMD AND G.SCHDL_END_YMD
AND DATE_FORMAT(G.SCHDL_BGNG_YMD, '%d') = #{dayOfMonth}
AND G.SCHDL_BGNG_YMD BETWEEN #{nowDate} AND #{sevenDaysLater}
AND DATE_FORMAT(G.SCHDL_END_YMD, '%Y-%m-%d') = #{today}
AND G.SCHDL_END_YMD BETWEEN #{nowDate} AND #{sevenDaysLater}
AND G.SCHDL_END_YMD < #{nowDate}

SELECT COUNT(*) FROM PRGRM_APP 
WHERE STUD_NO = #{studNumber} 
AND PRGRM_CD = #{proCode}

mapper.delete:
DELETE FROM PRGRM_APP 
WHERE STUD_NO = #{studNumber} 
AND PRGRM_CD = #{proCode}
