mapper.select:
SELECT SUM(count) AS total_count FROM ( SELECT COUNT(*) AS count FROM CSL_BOARD WHERE SUBSTRING(STUD_NO, 1, 2) = #{secd}
UNION ALL
SELECT COUNT(*) AS count FROM PRGRM_APP WHERE SUBSTRING(STUD_NO, 1, 2) = #{secd}
UNION ALL
SELECT COUNT(*) AS count FROM PROFESSER_CONSULTATION WHERE SUBSTRING(STUD_NO, 1, 2) = #{secd}
UNION ALL
SELECT COUNT(*) AS count FROM CSL_APPLY WHERE SUBSTRING(STUD_NO, 1, 2) = #{secd} ) AS cc;

mapper.update:
UPDATE STUD_INFO AS si
INNER JOIN USERS AS u ON si.stud_no = u.user_no
SET si.MBR_TELNO =
#{phoneNumber},
u.email = #{email}
WHERE si.stud_no = #{studNo}
