mapper.select:
SELECT
SUM(count) AS total_count
FROM (
SELECT COUNT(*) AS count
FROM CSL_BOARD
WHERE SUBSTRING(STUD_NO, 1, 2) = #{secd}

UNION ALL

SELECT COUNT(*) AS count
FROM PRGRM_APP
WHERE SUBSTRING(STUD_NO, 1, 2) = #{secd}

UNION ALL

SELECT COUNT(*) AS count
FROM PROFESSER_CONSULTATION
WHERE SUBSTRING(STUD_NO, 1, 2) = #{secd}

UNION ALL

SELECT COUNT(*) AS count
FROM CSL_APPLY
WHERE SUBSTRING(STUD_NO, 1, 2) = #{secd}
) AS cc;
